def odd_one_out(numberlist):

    from IPython.display import clear_output
    clear_output()

    while len(numberlist) >=3:

        '''
        Assuming a preconditon that there is ONE discrepancy in the list, there are three ways this WHILE loop can terminate.
        Exit condition 1: if there is 1 number left in the final slice
        Exit condition 2: if there are 2 numbers left in the final slice
        Exit condition 3: if a discrepancy in the slice is identified
        '''

        # take in number list and define a slice of three numbers

        slice=numberlist[0:3]
        #print('\nSlice=',slice,'\n')

        n1=slice[0]
        print(f'n1={n1}')

        n2=slice[1]
        print(f'n2={n2}')

        n3=slice[2]
        print(f'n3={n3}\n')



        # all odd or all even?

        if (n1%2 == 0 and n2%2 ==0 and n3%2 ==0) or (n1%2 != 0 and n2%2 !=0 and n3%2 !=0):
            print('All the same - go to next slice \n')
            last_in_slice=n3
            numberlist.pop(0)
            numberlist.pop(0)
            numberlist.pop(0)
            # print('updated numberlist: ',numberlist,'\n')
            print('last number in the slice: ',n3,'\n------------------\n')




        else:
            #Exit Condition 3
            print('Not the same - checking for odd one out\n')
            break



    if len(numberlist) == 2:

        # Exit condition 2: two numbers left in final slice

        slice=numberlist[0:2]
        print('\nSlice=',slice,'\n')

        n1=slice[0]
        print(f'n1={n1}')

        n2=slice[1]
        print(f'n2={n2}')

        n3=last_in_slice
        print(f'n3={n3}\n')


    if len(numberlist) == 1:

        # Exit condition 1: one number left in final slice

        slice=numberlist[0]
        print(f'\n>>>odd one out is {slice}<<<\n')



    else:

        # Exit condition 3: not all odd or all even

        if (n1+n2)%2 ==0:
            print(f'\n>>>odd one out is {n3}<<<\n')

        elif (n2+n3)%2 ==0:
            print(f'\n>>>odd one out is {n1}<<<\n')

        else:
            print(f'\n>>>odd one out is {n2}<<<\n')


